import { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import axios from "axios";
import { Stack, Pagination } from "@mui/material";

export default function Paginationdata() {
  const [userData, setUserData] = useState([]);

  // useEffect(() => {
  //   getdata();
  // }, []);

  // function getdata() {
  //   // fack api s fro data

  //   // const url = "https://api.instantwebtools.net/v1/airlines";
  //   // const url = "http://45.90.108.154/users";
  //   // const url = "https://jsonplaceholder.typicode.com/posts";
  //   axios.get(url).then((res) => {
  //     console.log(res.data);
  //     setUserData(res.data);
  //   });
  // }

  const handlePageChange = (pageNumber) => {
    console.log(`active page is ${pageNumber}`);
    axios
      .get(
        `https://jsonplaceholder.typicode.com/posts?_page=${pageNumber}&_limit=20`
      )
      .then((res) => {
        this.setState({
          data: res.data,
        });
      });
    setUserData({ activePage: pageNumber });
  };

  return (
    <>
      <div className=" container  table-responsive-lg mt-5 pt-1 pb-5 table  table-responsive-sm  table-responsive-md">
        <Table bordered striped hover>
          <thead>
            <tr>
              <th>id</th>
              <th> Name</th>
              <th> phone</th>
            </tr>
          </thead>
          <tbody>
            {userData.slice(0, 20).map((curElm, id) => {
              return (
                <tr key={curElm.id}>
                  <td> {curElm.id} </td>
                  <td> {curElm.body} </td>
                  <td> {curElm.title} </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
        <div style={{ float: "right" }}>
          <Stack spacing={2}>
            <Pagination
              activePage={this.state.activePage}
              itemsCountPerPage={10}
              totalItemsCount={450}
              pageRangeDisplayed={1}
              onChange={handlePageChange}
            />
          </Stack>
        </div>
      </div>
    </>
  );
}
